package com.java.conditional_statement;

public class If_else_if_4th {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int money=50000;
		if(money>=80000)
		{
			System.out.println("you can buy supra shoes");
		}
		else if(money>=60000)
		{
			System.out.println("you can buy ear jorder shoes");
		}
		else if(money>=40000)
		{
			System.out.println("you can buy normal shoes");
			
		}
		else
		{
			System.out.println("you can't buy");
		}

	}
 
}
