package com.java.operators;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Addition
		int a = 20;
		int b = 20;
		int c = a+b;
		System.out.println(c);
		//Subtraction
		int d = 50;
		int e = 20;
		int f = d-e;
		System.out.println(f);
		//multiplication
		int g,h,i;
		g=20;
		h=20;
		i=g*h;
		System.out.println(i);
		//division
		int j,k,l;
		j=15;
		k=4;
		l=j/k;
		System.out.println(l);
	}
		
}
